mapper.select:
SELECT 
MEMBERWISH.WISH_COUNT,MEMBERWISH.PRODUCT_NO,PRODUCT_BRAND,PRODUCT_CATEGORY ,PRODUCT_DATE ,DELIVERYFEE ,PRODUCT_FABRIC ,PRODUCT_FINALPRICE ,PRODUCT_PRICE ,PRODUCT_QUANTITY ,PRODUCT_SALE ,PRODUCT_SUBIMAGE_IDX ,PRODUCT_TITLE ,DELIVERYFEE_CHECK,PRODUCT_SALE_CHECK 
FROM MEMBERWISH,PRODUCT WHERE MEMBERWISH.PRODUCT_NO=PRODUCT.PRODUCT_NO AND MEMBERWISH.WISH_COUNT =1;
